    @GET
    @Path("/do")
    @Produces(MediaType.APPLICATION_JSON)
    public String doRequest(@QueryParam("url") String url) throws SQLException, IOException {
        return sendGET(url);
    }

    private static String sendGET(String url) throws IOException {
        url = StringEscapeUtils.unescapeHtml4(url).replaceAll(" ", "+");
        URL obj = new URL(url);
        System.out.println(url);
//        System.out.println(url.replaceAll("%26", "&").replaceAll(" ", "+"));
        HttpURLConnection con = (HttpURLConnection) obj.openConnection();
        con.setRequestMethod("GET");
        con.setRequestProperty("Cookie", "csrftoken=EqOSk2fN6MX1JaKD5ounbjJs6PrkYvwLE6a5z0nviqLo6aMyVaiSiLv3X3IQoA4a; sessionid=3a2tr6egroyddmikog7tlwc0ddcrttxr");
        con.setRequestProperty("Content-Type", "application/json");
        con.setRequestProperty("accept", "application/json");
        int responseCode = con.getResponseCode();
        System.out.println("GET Response Code :: " + responseCode);
        if (responseCode == HttpURLConnection.HTTP_OK) { // success
            BufferedReader in = new BufferedReader(new InputStreamReader(
                    con.getInputStream(), StandardCharsets.UTF_8));
            String inputLine;
            StringBuffer response = new StringBuffer();

            while ((inputLine = in.readLine()) != null) {
                response.append(inputLine);
            }
            in.close();
            System.out.println(response.toString());
            // print result
            return response.toString();
        } else {
            return new JSONObject("{\"error\": \"error\"}").toString();
        }

    }

